import React, { useRef, useEffect, useState} from 'react'
import grassTile from "./sprites/tiles/grass.png"
import pistolPlayer from "./sprites/player/pistol.png"

function Game(props) {

  const canvasRef = useRef(null)
  const [size, setSize] = useState({width: window.innerWidth, height: window.innerHeight})
  let playerPos = {x: 0, y: 0};
  let wDown = false;
  let aDown = false;
  let sDown = false;
  let dDown = false;
  let mousePos = {x: 0, y: 0};
  let playerRotation = 0;
  let bullets = []

  function shoot (x, y, spread) {
    direction = playerRotation * -1 - 90
  }

  const draw = (ctx, frameCount, sprites) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
    // Draw background
    const rotations = [0, Math.PI/2, Math.PI, Math.PI*3/2]
    for (let i = 0; i < size.width; i+= 48) {
      for (let j = 0; j < size.height; j+= 48) {
        ctx.save();

        ctx.translate(i, j)
        ctx.rotate(Math.PI*(i + j))
        ctx.drawImage(sprites.grassImg, 0, 0)
        ctx.restore()
      }
    }

    if (props.spawned) {
      // Draw player
      ctx.save()
      ctx.translate(ctx.canvas.width/2 + playerPos.x, ctx.canvas.height/2 + playerPos.y)
      ctx.rotate(playerRotation)
      ctx.drawImage(sprites.pistolPlayerImg, -24, -24)
      ctx.restore()
    }


  }

  useEffect(() => {

    const canvas = canvasRef.current
    const context = canvas.getContext('2d')
    const fps = 60;

    // Load images
    const grassImg = new Image();
    grassImg.src=grassTile;
    const pistolPlayerImg = new Image();
    pistolPlayerImg.src=pistolPlayer

    // Scale canvas with screen resize
    let resizeTimer;
    window.addEventListener('resize', (e) => {
      clearTimeout(resizeTimer)
      resizeTimer = setTimeout(() => setSize({width: window.innerWidth, height: window.innerHeight }), 100)
    })

    // Get mouse movements
    window.addEventListener("mousemove", (e) => {
      mousePos.x = e.clientX;
      mousePos.y = e.clientY;
    })

    // Track player inputs
    document.addEventListener('keydown', (event) => {
      if (event.key === 'w') wDown = true;
      if (event.key === 's') sDown = true;
      if (event.key === 'd') dDown = true;
      if (event.key === 'a') aDown = true;
    });

    document.addEventListener('keyup', (event) => {
      if (event.key === 'w') wDown = false;
      if (event.key === 's') sDown = false;
      if (event.key === 'd') dDown = false;
      if (event.key === 'a') aDown = false;
    });


    let frameCount = 0
    let animationFrameId

    // Game loop
    const render = () => {
      frameCount++

      // Move player
      if (wDown && props.spawned) playerPos.y -= 4;
      if (sDown && props.spawned) playerPos.y += 4;
      if (aDown && props.spawned) playerPos.x -= 4;
      if (dDown && props.spawned) playerPos.x += 4;

      // Get player rotation
      const playerScreenPos = {x: playerPos.x + context.canvas.width/2, y: playerPos.y + context.canvas.height/2}
      const dx = mousePos.x - playerScreenPos.x;
      const dy = mousePos.y - playerScreenPos.y;
      playerRotation = Math.atan(Math.abs(dy)/Math.abs(dx))
      if(dx < 0 && dy > 0) {
        playerRotation += Math.PI;
      }
      else if (dx < 0) {
        playerRotation *= -1
        playerRotation += Math.PI;
      }
      else if (dy > 0) {
        playerRotation *= -1;
        playerRotation += 0;
      }
      playerRotation += Math.PI/2;
      playerRotation *= -1
      console.log(playerRotation/Math.PI/2*360, dx, dy)
      draw(context, frameCount, {grassImg: grassImg, pistolPlayerImg: pistolPlayerImg})
      animationFrameId = setTimeout(render, 1000/fps)
    }
    render()

    return () => {
     clearTimeout(animationFrameId)
    }
  }, [draw])

  return (<canvas width={size.width} height={size.height} style={{zIndex: "-1", position: "fixed", left: "0vw", top: "0vw"}} ref={canvasRef} {...props}/>)
}

export default Game
