import React, { useRef, useEffect, useState} from 'react'
import grassTile from "./sprites/tiles/grass.png"

const Game = props => {

  const canvasRef = useRef(null)
  const [size, setSize] = useState({width: window.innerWidth, height: window.innerHeight})

  const draw = (ctx, frameCount, grassImg) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
    // Draw background
    for (let i = 0; i < size.width; i+= 80) {
      for (let j = 0; j < size.height; j+= 80) {
        ctx.drawImage(grassImg, i, j)
        ctx.rotate(Math.PI/2)
      }
    }

    ctx.fillStyle = "green";
    ctx.fill()
    ctx.beginPath()
    ctx.arc(50, 50, 20*Math.sin(frameCount*0.05)**2, 0, 2*Math.PI)

  }

  useEffect(() => {

    const canvas = canvasRef.current
    const context = canvas.getContext('2d')
    const grassImg = new Image();
    grassImg.src=grassTile;

    let resizeTimer;
    window.addEventListener('resize', (e) => {
      clearTimeout(resizeTimer)
      resizeTimer = setTimeout(() => setSize({width: window.innerWidth, height: window.innerHeight }), 100)
    })

    let frameCount = 0
    let animationFrameId

    //Our draw came here
    const render = () => {
      frameCount++
      draw(context, frameCount, grassImg)
      animationFrameId = window.requestAnimationFrame(render)
      console.log(size)
    }
    render()

    return () => {
      window.cancelAnimationFrame(animationFrameId)
    }
  }, [draw])

  return (<canvas width={size.width} height={size.height} style={{zIndex: "-1", position: "fixed", left: "0vw", top: "0vw"}} ref={canvasRef} {...props}/>)
}

export default Game
